import React, { useEffect } from "react";
import { useLocation } from "react-router";
import "./singlePost.css";
import post from "../../images/post.jpg";
import axios from "axios";

export default function SinglePost() {
	const location = useLocation();
	const path = location.pathname.split("/")[2];

	useEffect(() => {
		const getPost = async () => {
			const res = axios.get('/post/')
		}
	}, [path]);
	return (
		<div className="singlePost">
			<div className="singlePostWrapper">
				<img src={post} alt="" className="singlePostImg" />
				<h1 className="singlePostTitle">
					Lorem ipsum dolor sit amet.
					<div className="singlePostEdit">
						<i className="singlePostIcon far fa-edit"></i>
						<i className="singlePostIcon far fa-trash-alt"></i>
					</div>
				</h1>
				<div className="singlePostInfo">
					<span className="singlePostAuthor">
						Author: <b>Affaq</b>
					</span>
					<span className="singlePostDate">
						Date: <b>1 Hour Ago</b>
					</span>
				</div>
				<p className="singlePostDesc">
					Lorem ipsum dolor sit amet consectetur adipisicing elit. Tempora,
					laboriosam a reiciendis omnis maiores minima sint cumque fugiat
					voluptatum laudantium dignissimos iste, sunt aspernatur sapiente, ab
					sequi delectus autem id numquam illo atque quod! Cum voluptatem non
					reiciendis, reprehenderit, repellendus autem similique placeat enim
					natus officiis minima unde numquam quia neque animi praesentium magni.
					Lorem ipsum dolor sit amet consectetur adipisicing elit. Magnam, omnis
					eligendi! Odit corrupti natus rerum aliquid expedita, quae numquam
					labore.
				</p>
			</div>
		</div>
	);
}
